// import App from 'next/app'

import { Fragment } from "react";
import App, { Container, Head } from "next/app"; // eslint-disable-line

import Header from "../components/header";
import { useState } from "react";

const lightTheme = {
  bg: "#fafbfc",
  cardBG: "rgba(255, 255, 255, 1)",
  cardBoxShadow:
    "0 5px 10px rgba(154, 160, 185, 0.05), 0 15px 40px rgba(166, 173, 201, 0.2)",
  cardBoxHoverBG: "",
};
const darkTheme = {
  bg: "#363942",
  cardBg: "#363942",
  cardBoxShadow: "0 -2px 10px rgba(0, 0, 0, .5)",
  cardBoxHoverBG: "",
};

function MyApp({ Component, pageProps }) {
  const [theme, setTheme] = useState("light");
  const getTheme = (key) => {
    if (theme === "light") {
      return lightTheme[key] || "";
    } else {
      return darkTheme[key] || "";
    }
  };
  return (
    <Fragment>
      <Container>
        <Head>
          <title>Naveen DA Portfolio</title>
          <link
            rel="icon"
            href={"/favicon-" + theme + ".png"}
            type="image/png"
          />
          <link
            rel="stylesheet"
            href="https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"
          />
          <link
            href="https://fonts.googleapis.com/css2?family=Engagement&family=Nunito Sans&display=swap"
            rel="stylesheet"
          />
        </Head>
        <Header />

        <Component {...pageProps} />

        <style jsx global>{`
          html,
          body {
            padding: 0;
            margin: 0;
            font-family: "Nunito Sans";
            background: ${getTheme("bg")};
          }
          body {
            overflow-y: hidden;
          }
          @media only screen and (max-width: 600px) {
            body {
              overflow-y: auto !important;
            }
          }
          @keyframes backgroundGradient {
            0% {
              background-position: 50% 0%;
            }
            50% {
              background-position: 50% 100%;
            }
            100% {
              background-position: 50% 0%;
            }
          }
          * {
            box-sizing: border-box;
            padding: 0;
            margin: 0;
          }
          header .links li a {
            font-size: 12px;
            color: #333;
            text-decoration: none !important;
            background: linear-gradient(to right, #00d1b1 0%, #278fc6 98%);
            background-size: 0px 2px;
            background-repeat: no-repeat;
            background-position: left 85%;
            text-shadow: 0;
            transition: all 0.3s linear;
            padding-bottom: 5px;
          }

          header .links li a:hover {
            background-size: 100% 2px;
          }
        `}</style>
      </Container>
    </Fragment>
  );
}

// Only uncomment this method if you have blocking data requirements for
// every single page in your application. This disables the ability to
// perform automatic static optimization, causing every page in your app to
// be server-side rendered.
//
// MyApp.getInitialProps = async (appContext) => {
//   // calls page's `getInitialProps` and fills `appProps.pageProps`
//   const appProps = await App.getInitialProps(appContext);
//
//   return { ...appProps }
// }

export default MyApp;
